		{ text = "Can I stay with you?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Uh, what?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Can I stay with you? Please?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Of course!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Really?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "No.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Please! I don't wanna go back there! You don't know what it's like to be considered a freak!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Well, maybe you do, but that's why we gotta stick together. You gotta let me stay! Please! PLEASE!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "OKAY! Okay. But one night only.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ah! Thank you!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "What are you- No. No.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ah, this is gonna be fun. We can stay up late, swappin' manly stories, and in the mornin', I'm makin' waffles.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Groaning]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Where do, uh, I sleep?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "OUTSIDE!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, well, I guess that's cool.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "I mean, I don't know you, and you don't know me, I guess outside is best.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Sniffles] Here I go. Good night.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Door Slams]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Sighs] I mean, I do like the outdoors. I'm a donkey. I was born outside. I'll just be sitting by myself outside, I guess. You know. By myself, outside.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[img=music] I'm all alone, there's no one here beside me. [img=music]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
