		{ text = "*Sip*", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Sighs]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Squeezing]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
