		{ text = "[Match Lighting]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Eating]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Creaking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Sighs] I thought I told you to stay outside?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I am outside!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Clattering]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Scurrying]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Well, gents. It's a far cry from the farm, but what choice do we have?", r = 0.70, g = 0.70, b = 1.00, codes = "BOR-1" },
		{ text = "It's not home, but it'll do just fine.", r = 0.70, g = 0.70, b = 1.00, codes = "BOR-1" },
		{ text = "[Squishing]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "What a lovely bed.", r = 0.70, g = 0.70, b = 1.00, codes = "BOR-1" },
		{ text = "Got ya!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Sniffs] I found some cheese!", r = 0.70, g = 0.70, b = 1.00, codes = "BOR-1" },
		{ text = "Ow! [Grunts]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Bleh! Awful stuff.", r = 0.70, g = 0.70, b = 1.00, codes = "BOR-1" },
		{ text = "*Splat!*", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Grunts]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Is that you, Gorder?", r = 0.70, g = 0.70, b = 1.00, codes = "BOR-1" },
		{ text = "How did you know?", r = 0.70, g = 0.70, b = 1.00, codes = "BOR-1" },
		{ text = "Enough! What are you doing in my house?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Grunts] Hey!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Snickers]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, no, no, no. Dead broad off the table!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Where are we supposed to put her? The bed's taken.", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Huh?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Gasps]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "What?", r = 1.00, g = 0.70, b = 0.70, codes = "BOR-1" },
		{ text = "I live in a swamp. I put up signs. I'm a terrifying ogre!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "What do I have to do to get a little privacy!?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Aah!", r = 1.00, g = 0.70, b = 0.70, codes = "BOR-1" },
		{ text = "Oh no. Oh no. No!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Cackling]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "NO!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Grunts]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Cackling]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "What?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Quit it. Don't push.", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Pipe Playing]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Squeaking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "What are you doing in my swamp!?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Echoing] Swamp? Swamp? Swamp?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Gasping]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, dear!", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Whoa!", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Alright, get out of here. All of you, move it!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Come on. Let's go! Hapaya! Hapaya! Hey!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Quickly. Come on!", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "No, no! No, no. Not there. Not there.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Door Shuts]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Sighs]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Hey, don't look at me. I didn't invite them.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh gosh, no one invited us.", r = 1.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "What?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "We were forced to come here.", r = 1.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "By who?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Lord Farquaad. He huffed und he puffed und he...", r = 1.00, g = 0.80, b = 0.80, codes = "BOR-1" },
		{ text = "Signed an eviction notice.", r = 1.00, g = 0.80, b = 0.80, codes = "BOR-1" },
		{ text = "[Sighing] All right. Who knows where this Farquaad guy is?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Murmuring]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, I do! I know where he is!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Does anyone ELSE know where to find him?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Anyone at all?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Me! Me!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Anyone?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh! Oh, pick me! Oh, I know! I know! Me, me!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Sighs] Okay, fine.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Attention, all... fairy tale things. Do not get comfortable. Your welcome is officially worn out!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "In fact, I'm gonna see this guy Farquaad right now, and get you all off my land and back where you came from!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Cheering]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Tweeting]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh! You! You're comin' with me.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "All right, that's what I like to hear, man. Shrek and Donkey, two stalwart friends, off on a whirlwind big-city adventure.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "I love it!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[img=music] On the road again. [img=music] Sing it with me, Shrek.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[img=music] I can't wait to get on the road again. [img=music]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "What did I say about singing?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Well, can I whistle?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "No.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Well, can I hum it?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "All right, hum it.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Humming]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "*Clink!*", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Milk Pouring]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Footsteps]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Door Slam]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Gurgling]", r = 0.70, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "That's enough! He's ready to talk.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Coughing]", r = 0.70, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "[Laughing]", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ahem!", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Chains Clanking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Run, run, run, as fast as you can. You can't catch me, I'm the gingerbread man!", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "You're a monster.", r = 0.70, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "I'm not the monster here, you are! You and the rest of that fairy tale trash, poisoning my perfect world.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Now tell me, where are the others!?", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Eat me!", r = 0.70, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "[Spits]", r = 0.70, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "I've tried to be fair to you creatures. Now my patience has reached its end! Tell me, or I'll-", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "No! Not the buttons! Not my gumdrop buttons!", r = 0.70, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "All right, then. Who's hiding them?", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Okay. I'll tell you. Do you know the muffin man?", r = 0.70, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "The muffin man?", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "The muffin man.", r = 0.70, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "Yes, I know the muffin man, who lives on Drury Lane?", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Well, she's married to the muffin man.", r = 0.70, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "The muffin man?", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "The muffin man!", r = 0.70, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "She's married to the muffin man.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Door Opens]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "My lord! We've found it.", r = 1.00, g = 1.00, b = 0.80, codes = "BOR-1" },
		{ text = "Well, then what are you waiting for? Bring it in.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "*Clank!*", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Harp Playing]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Gasping]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ohh...", r = 0.70, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "Magic mirror.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Don't tell him anything!", r = 0.70, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "No!", r = 0.70, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "[Trash Can Shuts]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Whimpering]", r = 0.70, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "Evening.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Mirror, mirror, on the wall. Is this not the most perfect kingdom of them all?", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Well, technically, you're not a king.", r = 0.90, g = 0.90, b = 1.00, codes = "BOR-1" },
		{ text = "Uh, Thelonius.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Glass Shattering]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "You were saying?", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "What I mean is, uh, you're not a king yet. Heh. But you can become one. All you have to do is marry a princess.", r = 0.90, g = 0.90, b = 1.00, codes = "BOR-1" },
		{ text = "Go on.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Chuckling] So, just sit back and relax, my lord, because it's time for you to meet today's eligible bachelorettes!", r = 0.90, g = 0.90, b = 1.00, codes = "BOR-1" },
		{ text = "And here they are!", r = 0.90, g = 0.90, b = 1.00, codes = "BOR-1" },
		{ text = "Bachelorette number one is a mentally abused shut-in from a kingdom far, far away. She likes sushi and hot tubbing anytime.", r = 0.90, g = 0.90, b = 1.00, codes = "BOR-1" },
		{ text = "Her hobbies include cooking and cleaning for her two evil sisters. Please welcome Cinderella.", r = 0.90, g = 0.90, b = 1.00, codes = "BOR-1" },
		{ text = "Bachelorette number two is a cape-wearing girl from the land of fancy. Although she lives with seven other men, she's not easy.", r = 0.90, g = 0.90, b = 1.00, codes = "BOR-1" },
		{ text = "Just kiss her dead, frozen lips and find out what a live wire she is. Come on. Give it up for Snow White!", r = 0.90, g = 0.90, b = 1.00, codes = "BOR-1" },
		{ text = "And last, but certainly not the least, bachelorette number three is a fiery redhead, from a dragon-guarded castle surrounded by hot boiling lava!", r = 0.90, g = 0.90, b = 1.00, codes = "BOR-1" },
		{ text = "But don't let that cool you off. She's a loaded pistol who likes pina coladas and getting caught in the rain. Yours for the rescuing, Princess Fiona!", r = 0.90, g = 0.90, b = 1.00, codes = "BOR-1" },
		{ text = "So will it be, bachelorette number one, bachelorette number two, or bachelorette number three?", r = 0.90, g = 0.90, b = 1.00, codes = "BOR-1" },
		{ text = "[Drum Roll]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Two! Two!", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Three! Three!", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Two! Two!", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Three!", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Three? One? [Shudders] Three?", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Three! Pick number three, my lord!", r = 0.40, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Okay, okay! Uh, number three!", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Lord Farquaad, you've chosen Princess Fiona.", r = 0.90, g = 0.90, b = 1.00, codes = "BOR-1" },
		{ text = "[img=music] If you like pina coladas. And getting caught in the rain. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Princess Fiona.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] If you're not into yoga. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "She's perfect.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "All I have to do is just find someone who can go.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "But I probably should mention the little thing that happens at night.", r = 0.90, g = 0.90, b = 1.00, codes = "BOR-1" },
		{ text = "I'll do it.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Yes, but after sunset-", r = 0.90, g = 0.90, b = 1.00, codes = "BOR-1" },
		{ text = "Silence! I will make this Princess Fiona my queen, and Duloc will finally have the perfect king!", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Captain, assemble your finest men. We're going to have a tournament!", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "But that's it. That's it right there. That's Duloc.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "I told ya I'd find it.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "So, that must be Lord Farquaad's castle.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Uh-huh. That's the place.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Do you think maybe he's compensating for something? [Laughs]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Hmm. Hey, wait. Wait up, Shrek.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Hurry, darling. We're late. Hurry.", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Hey, you!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Screams]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Wait a second. Look, I'm not gonna eat ya. I just- I just-", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Whimpering]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Sighs]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Metal Clanking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Turnstile Clatters]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Turnstile Clattering]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oof! [Chuckles]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Sighs]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "It's quiet.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Too quiet.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Where is everybody?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Hey, look at this!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Whirring]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Clicking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] Welcome to Duloc such a perfect town. Here was have some rules, let us lay them down. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] Don't make waves, stay in line and we'll get along fine, Duloc is a perfect place. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] Please keep off of the grass, shine your shoes, wipe your... face. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] Duloc is, Duloc is, Duloc is a perfect place! [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Camera Click]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Wow. Let's do that again!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "No. No. No, no, no!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "No.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Trumpet Fanfare]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Cheering]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Brave knights. You are the best and brightest in all the land.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Humming]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Today one of you shall prove himself...", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "All right. You're going the right way for a smacked bottom.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Sorry about that.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Cheering]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "That champion shall have the honor- No, no, the privilege, to go forth and rescue the lovely Princess Fiona, from the fiery keep of the dragon.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "If for any reason the winner is unsuccessful, the first runner-up will take his place, and so on and so forth.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Some of you may die, but it's a sacrifice I am willing to make.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Cheering]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Let the tournament begin!", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Gasps]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "What is that?", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Eugh! It's hideous!", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ah, that's not very nice. It's just a donkey.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Huh?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Indeed. Knights, new plan! The one who kills the ogre will be named champion! Have at him!", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Get him!", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, hey! Now come on. Hang on now.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Go ahead! Get him!", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Can't we just settle this over a pint?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Kill the beast!", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "No? All right then.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Come on!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "*Clank!*", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Liquid Flowing]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] I don't give a damn about my reputation. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] You're living in the past, it's a new generation. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] A girl can do what she wants to do, and that's what I'm gonna do. And I don't give a damn about my bad reputation. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] Oh, no, no, no, no, no. Not me. Me, me, me. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Hey, Shrek, tag me! Tag me!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[img=music] And I don't give a damn about my reputation. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Metal Clank]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] Never said I wanted to improve my station. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ha! [Laughs]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] And I'm always feelin' good when I'm having fun. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Yeah!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] And I don't have to please no one. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "The chair! Give him the chair!", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] And I don't give a damn about my reputation. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] Oh, no, no, no, no, no. Not me. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] Me, me, me. Oh, no, no, no, no. Not me, not me. Not me. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Bell Ding]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Crowd Cheering]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Laughs] Oh, yeah! Ah! Ah! Thank you!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Thank you very much! I'm here till Thursday. Try the veal! Ha, ha!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Crossbows Cocking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Crowd Gasping]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Shall I give the order, sir?", r = 1.00, g = 1.00, b = 0.80, codes = "BOR-1" },
		{ text = "No, I have a better idea.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "People of Duloc! I give you our champion!", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Crowd Cheering]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "What?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Congratulations, ogre. You've won the honor of embarking on a great and noble quest.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Quest? I'm already on a quest, a quest to get my swamp back!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Your swamp?", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Yeah, my swamp! Where you dumped those fairy tale creatures!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Indeed.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "All right, ogre. I'll make you a deal. Go on this quest for me, and I'll give you your swamp back.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Exactly the way it was?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Down to the last slime-covered toadstool.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "And the squatters?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "As good as gone.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "What kind of quest?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Okay, let me get this straight.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "You're gonna go fight a dragon, and rescue a princess just so Farquaad will give you back a swamp, which you only don't have because he filled it full of freaks in the first place.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Is that about right?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "You know what? Maybe there's a good reason donkeys shouldn't talk.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I don't get it, Shrek. Why don't you just pull some of that ogre stuff on him? You know, throttle him, lay siege to his fortress, grind his bones to make your bread. You know, the whole ogre trip.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh, I know what. Maybe I could have decapitated an entire village, and put their heads on a pike, gotten a knife, cut open their spleen and drink their fluids.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Does that sound good to you?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Uh, no, not really, no.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "For your information, there's a lot more to ogres than people think.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Example?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Example? Okay, um. Ogres are like onions.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Sniffs] They stink?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Yes- No!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "They make you cry?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "No!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, you leave them out in the sun, they get all brown, start sproutin' little white hairs.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "No! Layers! Onions have layers. Ogres have layers! Onions have layers.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "You get it? We both have layers. [Sighs]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh, you both have layers. Oh.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Sniffs] You know, not everybody likes onions.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Cake! Everybody loves cakes! Cakes have layers.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "I don't care what everyone likes. Ogres. Are not. Like cakes.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "You know what else everybody likes? Parfaits.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Have you ever met a person, you say, 'Hey, let's get some parfait,' they say, 'No, I don't like parfait'? Parfaits are delicious.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "No! You dense, irritating, miniature beast of burden!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ogres are like onions! End of story. Bye-bye. See ya later.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Parfaits may be the most delicious thing on the whole damn planet.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "You know, I think preferred your humming.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Do you have a tissue or something? I'm making a mess. Just the word parfait makes me start slobbering.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[img=music] I'm on my way from misery to happiness today. Uh-huh, uh-huh, uh-huh, uh-huh. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] I'm on my way from misery to happiness today. Uh-huh, uh-huh, uh-huh, uh-huh. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] And everything that you receive up yonder is what you give to me the day I wander, I'm on my way. I'm on my way. I'm on my way. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Phew! Shrek! Did you do that?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Man, you gotta warn somebody before you just crack one off, my mouth was open and everything.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Believe me, Donkey, if it was me, you'd be dead.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Sniffs] It's brimstone. We must be getting close.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Yeah, right, brimstone. Don't be talking about it's the brimstone.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "I know what I smell. It wasn't no brimstone. It didn't come off no stone either.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Rumbling]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Lava Spouting]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Sure, it's big enough, but look at the location. [Laughing]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek? Remember when you said ogres have layers?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh, aye.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Well, I have a bit of a confession to make.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Donkeys don't have layers. We wear our fear right out there on our sleeves.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Wait a second. Donkeys don't have sleeves.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "You know what I mean.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh, you can't tell me you're afraid of heights?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "No, I'm just a little uncomfortable about being on a rickety over a boiling lake of lava!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Come on, Donkey. I'm right here beside ya, okay. For emotional support.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "We'll just tackle this thing together one little baby step at a time.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Really?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Really, really.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Okay, that makes me feel so much better.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Just keep moving. And don't look down.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Okay, don't look down. Don't look down. Don't look down. Keep on moving. Don't look down.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "*Crack!*", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Gasps] Shrek! I'm lookin' down! Oh god, I can't do this! Just let me off right now. Please.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "But you're already halfway.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "But I know that half is safe!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Okay, fine. I don't have time for this. You go back.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek, no! Wait!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Donkey- Let's have a dance then, shall we?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Bridge Creaking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Don't do that!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh, I'm sorry. Do what? Oh, this?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Bridge Creaking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Yes, that!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "This? This, do it. Okay.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Bridge Creaking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Screams] No, Shrek! No! Stop it!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "You said do it. I'm doin' it.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I'm gonna die. I'm gonna die. Shrek, I'm gonna die.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "...", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "That'll do, Donkey. That'll do.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Cool", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "So, where is this fire-breathing pain-in-the-neck anyway?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Inside, waiting for us to rescue her. [Chuckles]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I was talkin' about the dragon, Shrek.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Water Dripping]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Wind Howling]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Whispering] You afraid?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "No, but- Shh.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, good. Me neither.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Gasps] 'Cause there's nothin' wrong with bein' afraid. You know, fear's a sensible response to an unfamiliar situation. Unfamiliar dangerous situation, I might add.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "And with a dragon that breathes fire and eats knights and breathes fire, it sure doesn't mean you're a coward if you're a little scared, you know what I mean.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "'Cause I sure as heck ain't no coward. I know that.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Clanging Armor]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Gasps]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Donkey, two things, okay?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Shut. Up.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Now go over there and see if you can find any stairs.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Stairs? I thought we was lookin' for the princess.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "The princess will be up the stairs in the highest room in the tallest tower.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "What makes you think she'll be there?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "I read it in a book once.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Cool. You handle the dragon. I'll handle the stairs. Oh, I'll find those stairs.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "I'll whip their butt too. Those stairs won't know which way they're goin'.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Door Creaking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "I'm gonna take drastic steps. Kick it to the curb. Don't mess with me. I'm the stair master.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "I've mastered the stairs. I wish I had a step right here, right here. I'd step all over it.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Growling]", r = 0.60, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Well, at least we know where the princess is, but where's the-", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Dragon! [Screams]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Stomping]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Roars]", r = 0.60, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Flames Shooting]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Donkey, look out! Ah!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Screams]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Whimpering]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Got ya!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Roars]", r = 0.60, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Gasps]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Whoa! Whoa! Whoa! [Screaming]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "*Crash!*", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Gasps] Oh! Aah! Aah!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Roars]", r = 0.60, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "No. Oh, no. No! [Screams] Oh, what large teeth you have.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Growls]", r = 0.60, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "I mean, I mean, white sparkling teeth. I know you probably hear this all the time from your food, but you must bleach or something, 'cause that is one dazzling smile you got there.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "And do I detect a hint of minty freshness? And you know what else? You know what else? You're- You're a girl dragon!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh, sure! I mean, of course you're a girl dragon. 'Cause, you're just reeking a feminine beauty. What's the matter with you? You got something in your eye?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Ooh. Oh. Oh. Man, I'd really love to stay, but, you know, I'm, uh- [Coughs] I'm an asthmatic, and I don't know if it'd work out if you're gonna blow smoke rings and stuff.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Shrek! [Gasps] No! Shrek!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Stomping]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek! Shrek!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Groans]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Vocal Chorus]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ah! Oh!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Wake up!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "What?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Are you Princess Fiona?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I am, awaiting a knight so bold as to rescue me.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, that's nice. Now, let's go!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "But, wait, Sir Knight. This be-ith our first meeting.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Should it not be a wonderful, romantic moment?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Yeah. Sorry, lady. There's no time.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Hey, wait. What are you doing?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "You know, you should sweep me off my feet, out yonder window, and down a rope onto your valiant steed.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "You've had a lot of time to plan this, haven't you?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Mm-hmm. [Screams]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "*Crash!*", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Grunts]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "But we have to savor this moment! You could recite an epic poem for me. A ballad? A sonnet! A limerick? Or something!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "I don't think so.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Can I at least know the name of my champion?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Um, Shrek.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Sir  Shrek. [Clears Throat] I pray that you take this favor as a token of my gratitude.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Thanks.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Roaring]", r = 0.60, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "You didn't slay the dragon?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "It's on my to-do list. Now, come on!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Screams] But this isn't right! You're meant to charge in, sword drawn, banner flying! That's what all the other knights did!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Yeah, right before they burst into flame!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "You know, that's not the point! Oh! Wait. Where are you going? The exit's over there.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Well, I have to save my ass.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "What kind of knight are you?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "One of a kind.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Door Opening]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Slow down. Slow down, baby, please. I believe it's healthy to get to know someone over a long period of time. Just call me old-fashioned, you know.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Laughs] I don't want to rush into a physical relationship. I'm not emotionally ready for a commitment of, uh, this- Magnitude really is the word I'm looking for.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Magnitude- Hey, that is unwanted physical contact.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Hey, what are you doing? Okay, okay. Let's just back up a little and take this one step at a time.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "We really should get to know each other first as friends or maybe even as pen pals.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "'Cause I'm the road a lot, but I just love receiving cards, and- I'd really love to stay, but- Hey, hey, hey! Don't do that!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "That's my tail! That's my personal tail. You're gonna tear it off. I don't give permission to- Wait.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "What are you gonna do with that? Hey, now. No way. No! No! No, no! No. No, no, no! No! Oh!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Chain Clanking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oof!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "*Smooch!*", r = 0.60, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Growls]", r = 0.60, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Chain Falling]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Roars]", r = 0.60, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Flames Shooting]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ah!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Hi, Princess!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "It talks!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Yeah, it's getting him to shut up that's the trick!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Screams]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Cracking Nuts]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Pained Groan]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Roaring]", r = 0.60, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Gasping]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Flames Shooting]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Okay, you two, head for the exit!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "*Shing!*", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "I'll take care of the dragon.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Chains Clanging]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Echoing] RUUUUUUUUUUUUN!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Flames Shooting]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Ropes Snapping]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Screams]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Whoa! Ah!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Roaring]", r = 0.60, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Chains Whipping]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Screams]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Roaring]", r = 0.60, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Sad Whimpers]", r = 0.60, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Distant Roars]", r = 0.60, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "You did it! You rescued me! You're amazing. You're- You're wonderful.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "You're... a little unorthodox, I'll admit.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "But thy deed is great, and thine heart is pure. I am eternally in your debt.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Clearing Throat]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "And where would be a brave knight be without his noble steed?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "All right, I hope you heard that. She called me a noble steed.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "She think I'm a steed.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Laughs] The battle is won. You may remove your helmet, good Sir Knight.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Uh, no.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Why not?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "I... I have helmet hair.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Please. I would'st look upon the face of my rescuer.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "No, no, you wouldn't'st.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "But how will you kiss me?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "What? That wasn't in the job description.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Maybe it's a perk!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "No, it's destiny. Oh, you must know how it goes.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "A princess locked in a tower and beset by a dragon, is rescued by a brave knight, and then they share true love's first kiss.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Hmm? With Shrek? You think- Wait. Wait a second.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "You think that Shrek is your true love?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Well, yes.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Laughing]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Laughing]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "You think Shrek is your true love!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "What is so funny?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Let's just say I'm not your type, okay?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Of course you are. You're my rescuer. Now- Now remove your helmet.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Look. I really don't think this is a good idea.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Just take off the helmet.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "I'm not going to.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Take it off.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "No!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Now!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Okay! Easy. As you command, Your Highness.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Helmet Creaking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "You- You're... an ogre.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, you were expecting Prince Charming.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Well, yes, actually.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, no. This is all wrong. You're not supposed to be an ogre.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Sighs] Princess, I was sent to rescue you by Lord Farquaad, okay. He's the one who wants to marry you.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Well, then why didn't he come to rescue me?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Good question. You should ask him that when we get there.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "But I have to be rescued by my true love. Not by some ogre and- and his pet.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Well, so much for noble steed.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Look, Princess, you're not making my job any easier.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Well, I'm sorry, but your job is not my problem.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "You can tell Lord Farquaad that if he wants to rescue me properly, I'll be waiting for him right here.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Hey! I'm no one's messenger boy, all right?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I'm a delivery boy.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "You wouldn't dare.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Ah! Put me down!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Ya comin', Donkey?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I'm right behind ya.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Put me down, or you will suffer the consequences!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "This is not dignified! Put me down! [Screams]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Okay, so here's another question. Say there's a woman that digs you, right? But you don't really like her that way.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Now how do you let her down real easy so her feelings aren't hurt, but you don't get burned to a crisp and eaten?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "How do you do that?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "You just tell her she's not your true love. Everyone knowest what happens when you find your-", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Grunts] Hey!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Sighs] The sooner we get to Duloc the better.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, yeah. You're gonna love it there, Princess? It's beautiful!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "And what of my groom-to-be? Lord Farquaad? What's he like?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Well, let me put this way, Princess.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "*Thud!*", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Men of Farquaad's stature are in... short supply. [Laughs]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I don't know, Shrek. There are those who think... little of him.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Laughing]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Laughing]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Stop it. Stop it, both of you. You know, you're just jealous you can never measure up to a great ruler like Lord Farquaad.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Yeah, well, maybe you're right, Princess. But I'll let you do the 'measuring' when you see him tomorrow.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Tomorrow? It'll take that long? Shouldn't we stop to make camp?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "No, that'll take longer.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "We can keep going.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "But there's robbers in the woods.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Whoa! Time out, Shrek! Camping's definitely startin' to sound good.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Hey, come on. I'm scarier than anything we're going to see in this forest.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I need to find somewhere to camp NOW!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Rock Sliding]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Grunting] Hey! Over here.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek, we can do better than that. Now, I don't think this is fit for a princess.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "No, no, it's perfect. It just needs a few homey touches.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Homey touches? Like what?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Wood Cracking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "A door?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Well, gentlemen, I bid thee good night.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "You want me to come in there and read you a bedtime story? 'Cause I will.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "I said good night!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Rock Sliding]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek, what are you doing?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Laughs] I just- You know...", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, come on. I was just kidding.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "And, uh, that one, that's Throwback. The only ogre to ever spit over three wheat fields.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Right. Yeah.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Hey, can you tell my future from these stars?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Well, the stars don't tell the future, Donkey. They tell stories.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Look, there's Bloodnut, the Flatulent. You can guess what he's famous for. [Chuckling]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I know you're making this up.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "No, look. There he is, and there's the group of hunters running away from his stench.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Man, that ain't nothin' but a bunch of little dots.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "You know, Donkey, sometimes things are more than they appear. Hmm?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Forget it.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Yawns] Hey, Shrek, what we gonna do when we get our swamp anyway?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Our swamp?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "You know, when we're through rescuing the princess and all that stuff.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "We? Donkey, there's no 'we.' There's no 'our.' There's just me and my swamp.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "And the first thing I'm gonna do is build a ten-foot wall around my land.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "You cut me deep, Shrek. You cut me real deep just now.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "You know what I think? I think this whole wall thing is just a way to keep somebody out.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "No. Do ya think?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Are you hidin' something?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Never mind, Donkey.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh! This is another one of those onion things, isn't it?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "No, this is one of those drop-it-and-leave-it-alone things.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Why don't you want to talk about it?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Why do you want to talk about it?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Why are you blocking?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "I'm not blocking.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh yes, you are.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Donkey, I'm warning you.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Who you trying to keep out? Just tell me that, Shrek. Who?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Everyone! Okay!?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh, now we're gettin' somewhere.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh! For the love of Pete!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "What's your problem? What you got against the whole world anyway, huh?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Look, I'm not the one with the problem, okay? It's the world that seems to have a problem with me. ", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "People take one look at me and go, 'Aah! Help! Run! A big, stupid, ugly ogre!'", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Sighs] They judge me before they even know me. That's why I'm better off alone.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "You know what? When we met, I didn't think you was just a big, stupid, ugly ogre.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Yeah, I know.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "So, uh, are there any donkeys up there?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Well, there's, um... Gabby, the Small and Annoying.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Okay, okay, I see- I see it now. Yeah, the big shiny one, right there. That- That one there?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "That's the moon.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, okay.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Again. Show me again.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Rewinding Sound]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Mirror, mirror, show her to me. Show me the princess.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Hmm.", r = 0.90, g = 0.90, b = 1.00, codes = "BOR-1" },
		{ text = "[Rewinding Sound]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "*Sip*", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ah. Perfect.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Inhales]", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Snoring]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Snoring]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Vocalizing]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Bird Whistling]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Vocalizing Continues]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Whistling Continues]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[High-Pitched Vocalizing]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[High-Pitched Whistling]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "*Pop!*", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Sizzling]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Sniffing] [Yawns]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Mmm, yeah, you know I like it like that.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Come on, baby. I said I like it.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Donkey, wake up.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Huh? What?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Wake up.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "What?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Morning. Um, how do you like your eggs?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Good morning, Princess!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "What's all this about?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "You know, we kind of got off to a bad start yesterday.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "And I wanted to make it up to you. I mean, after all, you did rescue me.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Uh, thanks.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Sniffing]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Well, eat up. We've got a big day ahead of us.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Belches]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "What? It's a compliment.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Better out than in, I always say. [Laughs]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Well, it's no way to behave in front of a princess.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Belches]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Thanks.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "She's as nasty as you are.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Laughs] You know, you're not exactly what I expected.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Well, maybe you shouldn't judge people before you get to know them.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Vocalizing]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "La liberte! Hey!", r = 0.80, g = 1.00, b = 0.50, codes = "BOR-1" },
		{ text = "Princess!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ha, ha, ha!", r = 0.80, g = 1.00, b = 0.50, codes = "BOR-1" },
		{ text = "Wait, wait. What are you doing?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Be still, mon cherie, for I am your savior!", r = 0.80, g = 1.00, b = 0.50, codes = "BOR-1" },
		{ text = "And I am rescuing you from this green... [Kissing Sounds]", r = 0.80, g = 1.00, b = 0.50, codes = "BOR-1" },
		{ text = "Beast.", r = 0.80, g = 1.00, b = 0.50, codes = "BOR-1" },
		{ text = "[Groans]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Hey! That's my princess. Go find your own!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Please, monster! Can't you see I'm a little busy here?", r = 0.80, g = 1.00, b = 0.50, codes = "BOR-1" },
		{ text = "Look, pal. I don't know who you think you are!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Oh! Of course! How rude. Please let me introduce myself.", r = 0.80, g = 1.00, b = 0.50, codes = "BOR-1" },
		{ text = "Oh, Merry Men! [Laughs]", r = 0.80, g = 1.00, b = 0.50, codes = "BOR-1" },
		{ text = "[Accordion Playing]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
