		{ text = "[img=music] So! [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] When an ogre in the bush grabs a lady by the tush, that's bad. [img=music]", r = 0.80, g = 1.00, b = 0.50, codes = "BOR-1" },
		{ text = "[img=music] That's bad. That's bad. That's bad. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
