		{ text = "Hold the phone.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Grunts]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh! Whoa, whoa, whoa. Hold on now. Where did that come from?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "What?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "That! Back there. That was amazing! Where did you learn that?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Well- [Chuckles] When one lives alone, uh, one has to learn these things in case there's a-", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "There's an arrow in your butt!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "What? Oh, would you look at that?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, no. This is all my fault. I'm so sorry.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Why? What's wrong?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Shrek's hurt.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek's hurt? Shrek's hurt!?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh, no, Shrek's gonna die!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Donkey, I'm okay.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, you can't do this to me, Shrek. I'm too young for you to die.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Keep your legs elevated. Turn your head and cough. Does anyone know the Heimlich?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Donkey! Calm down. If you want to help Shrek, run into the woods and find me a blue flower with red thorns.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Blue flower, red thorns. Okay. I'm on it. Blue flower, red thorns. Blue flower, red thorns.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Don't die, Shrek, and if you see a long tunnel, stay away from the light!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Donkey!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Okay, okay. Blue flower, red thorns. Blue flower, red thorns.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "What are the flowers for?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "For getting rid of Donkey.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Ah.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Now you hold still, and I'll yank this thing out.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Ow! Hey! Easy with the yankin'.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I'm sorry, but it has to come out.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "No, no, it's tender.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Now, hold on. What you're doing is the opposite of help.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Don't move.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Okay, look, look, time out.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Would you- [Grunts] Okay. What do you propose we do?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Blue flower, red thorns. Blue flower, red thorns. Blue flower, red thorns.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "This would be so much easier if I wasn't color-blind! Blue flower, red thorns.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Ow!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Hold on, Shrek! I'm comin'!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Ow! Not good.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Okay. Okay, I can nearly see the head.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Grunts]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "It's just about-", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Ow! Ohh!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ahem.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Nothing happened. We were just, uh-", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Look, if you wanted to be alone, all you had to do was ask, okay.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh, come on! That's the last thing on my mind!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "The princess here was just- Ugh!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ow.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Hey, what's that? [Nervous Chuckle]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "That's- Is that blood? [Sighs]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
