		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] My beloved monster and me. We go everywhere together. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] Wearin' a raincoat that has four sleeves, gets us through all kinds of weather. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Tree Whips]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ah!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[img=music] She will always be the only thing. That comes between me and the awful sting. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] That comes from living in the world that's so damn mean. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Croaking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
