		{ text = "[Hissing]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Inflating Balloon]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Rubber Squeaking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Hey!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] La-la, la-la, la-la-la-la. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Laughing]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Laughing]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] La-la-la-la, la-la. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "There it is, Princess. Your future awaits you.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "That's Duloc?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Yeah, I know.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "You know, Shrek thinks Lord Farquaad's compensating for something, which I think means he has a really- Ow!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Um, I, uh- I guess we better move on.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Sure. But, Shrek?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "I'm- I'm worried about Donkey.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Blubbering]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "What?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I mean, look at him. He doesn't look so good.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "What are you talking about? I'm fine.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Well, that's what they always say, and then- then next thing you know, you're on your back.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Dead.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "You know, she's right. You look awful. Do you want to sit down?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, you know what, I'll make you some tea.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Well, I didn't want to say nothin', but I got this twinge in my neck, and when I turn my head like this, look.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Crunching Bones]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ow! See?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Who's hungry? I'll find us some dinner.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I'll get the firewood.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Hey, where you goin'? Oh, man, I can't feel my toes!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "...", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "I don't have any toes!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "I think I need a hug.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Munching]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Mmm. Mmm. This is good. This is really good. What is this?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "A weedrat. Rotisserie style.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "No kidding. Well, this is delicious.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Well, they're also great in stews. Now, I don't mean to brag, but I make a mean weedrat stew. [Chuckling]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Sighs] I guess I'll be dining a little differently tomorrow night.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Gulps] Maybe you can come visit me in the swamp sometime.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I'll cook all kinds of stuff for you. Swamp toad soup, fish eye tartare- you name it.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Chuckles] I'd like that.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "*Slurp!*", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Laughs]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] See the pyramids along the Nile. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Um, Princess?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] Watch the sunrise from a tropical isle. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Yes, Shrek?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "I, um, I was wondering.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] Just remember, darling all the while. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Are you-", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] You belong to me. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Sighs] Are you gonna eat that?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Chuckles]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Man, isn't this romantic? Just look at that sunset.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Sunset? Oh, no! I mean, it's late. I-It's very late.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "What?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Wait a minute. I see what's goin' on here.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "You're afraid of the dark, aren't you?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Yes! Yes, that's it. I'm terrified. You know, I'd better go inside.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Don't feel bad, Princess. I used to be afraid of the dark, too, until-", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Hey, no, wait. I'm still afraid of the dark.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Sighs]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Good night.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Good night.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Door Creaks]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ohh! Now I really see what's goin' on here.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh, what are you talkin' about?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I don't even wanna hear it. Look, I'm an animal, and I got instincts, and I know that two were diggin' on each other. I could feel it.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh, you're crazy. I'm just bringing her back to Farquaad.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, come on, Shrek. Wake up and smell the pheromones. Just go on in and tell her how you feel.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "I- There's nothing to tell. Besides, even if I did tell her that, well, you know-", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "And I'm not sayin' I do 'cause I don't. She's a princess, and I'm...", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "An ogre?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Yeah. An ogre.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Hey, where you goin'?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "To get... more firewood. [Sighs]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Princess? Princess Fiona?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Princess, where are you?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Bats Flying]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Princess?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Creaking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Gasps] It's very spooky in here. I ain't playing no games.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "*Crash!*", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Screams]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Aah!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "No, no!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Oh no, help!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Shh!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek! Shrek! Shrek!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "No, it's okay. It's okay.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "What did you do with the princess?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Donkey, shh, I'm the princess.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Aah!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "It's me, in this body.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, my God! You ate the princess! Can you hear me?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Donkey!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Listen, keep breathing!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "No!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "I'll get you out of there!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Shrek! Shrek! Shrek!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Shh...", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Muffled] Shrek!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "This is me.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Princess? What happened to you? You're, uh, uh, uh, different.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "I'm ugly, okay?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Well, yeah! Well, was it something that you ate? 'Cause I told Shrek those rats was a bad idea. You are what you eat, I said. Now-", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "No. I- I've been this way as long as I can remember.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "What do you mean? Look, I ain't never seen you like this before.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "It only happens when the sun goes down.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "'By night one way, by day another. This shall be the norm, until you find true love's first kiss, and then take love's true form.'", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Ah, that's beautiful. I didn't know you wrote poetry.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "It's a spell.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Sighs] When I was a little girl, a witch cast a spell on me. Every night I become this. This horrible, ugly beast!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Water Splashing]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "I was placed in a tower to await the day my true love would rescue me.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "That's why I have to marry Lord Farquaad tomorrow, before the sun sets and he sees me, like this.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Sobbing]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "All right, all right. Calm down. Look, it's not that bad. You're not that ugly.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Well, I ain't gonna lie. You are ugly. But you only look like this at night. Shrek's ugly 24-7.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "But, Donkey, I'm a princess, and this is not how a princess is meant to look.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Sobbing]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Princess, how 'bout if you don't marry Farquaad?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "I have to. Only my true love's kiss can break the spell.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "But, you know, um, you're kind of an ogre, and Shrek... Well, you got a lot in common.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Shrek?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Princess, I- Uh, how's going, first of all? Good? Um, good for me too.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I'm okay. I saw this flower and thought of you because it's pretty and- well, I don't really like it, but I thought you might like it 'cause you're pretty.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "But I like you anyway. I'd- uh, uh- [Sighs] I'm in trouble. Okay, here we go.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I can't just marry whoever I want. Take a good look at me, Donkey.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "I mean, really, who could ever love a beast so hideous and ugly? 'Princess' and 'ugly' don't go together.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "That's why I can't stay here with Shrek. My only chance to live happily ever after is to marry my true love.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Sigh]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Don't you see, Donkey? That's just how it has to be.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Petals Rustling]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "It's the only way to break the spell.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Well, you at least gotta tell Shrek the truth.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "No! You can't breathe a word. No one must ever know.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "What's the point of being able to talk if you gotta keep secrets?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Promise you won't tell. Promise!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "All right, all right. I won't tell him. But you should.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Door Creaking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "I just know before this is over, I'm gonna need a whole lot of serious therapy. Look at my eye twitchin'.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Snoring]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "I tell him, I tell him not. I tell him, I tell him not. I tell him.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek! Shrek, there's something I want-", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Sparkling]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek. Are you all right?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Perfect! Never been better.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I- I don't- There's something I have to tell you.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "You don't have to tell me anything, Princess. I heard enough last night.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "You heard what I said?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Every word.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I thought you'd understand.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, I understand. Like you said, 'Who could love a hideous, ugly beast?'", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "But I thought that wouldn't matter to you.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Yeah? Well, it does.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Sigh]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Horse Whinny]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ah, right on time.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Princess, I've brought you a little something.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Fanfare]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Yawns] What'd I miss? What'd I miss?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "...", r = 1.00, g = 1.00, b = 0.80, codes = "BOR-1" },
		{ text = "[Muffled] Who said that? Couldn't have been a donkey.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Princess Fiona.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "As promised. Now hand it over.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Very well, ogre. The deed to your swamp, cleared out, as agreed. Take it and go before I change my mind.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Forgive me, Princess, for startling you, but you startled me, for I have ever seen such a radiant beauty before.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "I am Lord Farquaad.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Lord Farquaad? Oh. Uh, no, no.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Forgive ME, my lord, for I was just saying, a short... farewell.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, that is so sweet. You don't have to waste good manners on the ogre. It's not like it has feelings.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "No, you're right. It doesn't.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Princess Fiona, beautiful, fair, flawless Fiona. I ask your hand in marriage.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Gasps]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Will you be the perfect bride for the perfect groom?", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Lord Farquaad, I accept. Nothing would make-", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Excellent! I'll start the plans, for tomorrow we wed!", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "No!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "I mean, uh, why wait? Let's get married today before sunset.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, anxious, are we? You're right. The sooner, the better.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Fingers Snapping]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "There's so much to do! There's the caterer, the cake, the band, the guest list. Captain, round up some guests!", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Fare thee well, ogre.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek, what are you doing? You're letting her get away.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Yeah? So what?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek, there's something about her that you don't know. Look, I talked to her last night. She's-", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Yeah, I know you talked to her last night. You're great pals, aren't ya?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Now, if you two are such good friends, why don't you follow HER home?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek, I- I wanna go with you.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Hey, I told you, didn't I? You're not coming home with me. I live alone! My swamp! Me! Nobody else! Understand!? Nobody!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Especially useless, pathetic, annoying, talking donkeys!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "But... I thought-", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Yeah. You- You know what? You thought wrong!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek...", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] I heard there was a secret chord, that David played, and it pleased the Lord. But you don't really care for music, do ya? [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] It goes like this the fourth, the fifth, the minor fall, the major lift. The baffled king composing hallelujah. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] Hallelujah, hallelujah, hallelujah, hallelujah. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] Baby, I've been here before, I know this room I've walked this floor, I used to live alone before I knew you. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] I've seen your flag on the marble arch, but love is not a victory march. It's a cold and it's broken hallelujah. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] Hallelujah, hallelujah, hallelujah, hallelujah. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] And all I ever learned from love is how to shoot at someone who outdrew you. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] And it's not a cry you can hear at night, it's not somebody who's seen the light. It's a cold and it's a broken hallelujah. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] Hallelujah, hallelujah, hallelujah, hallelujah. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Thumping]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Donkey?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Grunts]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "What are you doing?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I would think, of all people, you would recognize a wall when you see one.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Well, yeah. But the wall's supposed to go around my swamp, not through it.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "It is. Around your half. See, that's your half, and this is my half.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh! Your half. Hmm.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Yes, my half. I helped rescue the princess. I did half the work. I get half the booty.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Now hand me that big old rock, the one that looks like your head.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Back off!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "No, you back off.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "This is my swamp!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "OUR swamp.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Let go, Donkey!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "You let go.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Stubborn jackass!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Smelly ogre.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Fine!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Hey, come back here. I'm not through with you yet.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Well, I'm through with you!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Uh-uh. You know, with you it's always, 'Me, me, me!' Well, guess what! Now it's my turn!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "So you just shut up and pay attention! You are mean to me. You insult me and you don't appreciate anything that I do!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "You're always pushing me around or pushing me away.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh, yeah? Well, if I treated you so bad, how come you came back?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Because that's what friends do! They forgive each other!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh, yeah. Yeah. You're right, Donkey. I forgive you.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "For stabbin' me in the back!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Door Slam]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ohh! You're so wrapped up in layers, onion boy, you're afraid of your own feelings.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Go away!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "See? There you are, doing it again just like you did to Fiona. All she ever do was like you, maybe even love you.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Love me? She said I was ugly! A hideous creature. I heard the two of you talking.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "She wasn't talkin' about you. She was talkin' about... uh, somebody else.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Door Creaking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "She wasn't talking about me? Well, then who was she talking about?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Uh-uh, no way. I ain't saying anything. You don't wanna listen to me. Right? Right?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Donkey!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "No!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Okay, look. I'm sorry, all right?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Hmph.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Sighs] I'm sorry.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I guess I am just a big, stupid, ugly ogre. Can you forgive me?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Hey, that's what friends are for, right?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Right. Friends?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Friends.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "So, um, what did Fiona say about me?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "What are you asking me for? Why don't you just go ask her?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "The wedding! We'll never make it in time.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ha, ha, ha! Never fear, for where there's a will, there's a way, and I have a way.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Whistles]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Wings Flapping]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Growling]", r = 0.60, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Donkey?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Laughs] I guess it's just my animal magnetism.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Laughing] Aw, come here, you.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "All right, all right. Don't get all slobbery. No one likes a kiss ass.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "All right, hop on and hold on tight. I haven't had a chance to install the seat belts yet.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Laughs]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Laughing] Whoo!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Church Bells Ringing]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "People of Duloc, we gather here today, to bear witness, to the union...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Of our new king-", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Um, excuse me?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Could we just skip ahead to the 'I do's'?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Chuckling] Go on.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "*Thud!*", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Guards Exclaiming]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Go ahead, have some fun. If we need you, I'll whistle. How about that?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek, wait, wait! Hey! Wait a minute! Look, you wanna do this right, don't you?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "What are you talking about?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "There's a line, there's a line you gotta wait for.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "The preacher's gonna say, 'Speak now or forever hold your peace.' That's when you say, 'I object!'", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh, I don't have time for this!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Hey wait, wait, what are you doing? Listen to me! Look, you love this woman, don't you?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Yes.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "You wanna hold her?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Yes.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Please her?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Yes!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] Then you got to, got to try a little tenderness! [img=music]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "The chicks love that romantic crap!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "All right! Cut it out. When does this guy say the line?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "We gotta check it out.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "And so, by the power vested in me...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Grunting]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "What do you see?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "The whole town's in there.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "I now pronounce you husband and wife...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "They're at the altar.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "King and queen...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Gasps] Mother Fletcher! He already said it.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Oh, for the love of Pete!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "*Thud!*", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Grunts]", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Door Slams Open]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "I object!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Gasps]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, now what does he want?", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Crowd Clamoring]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Hi, everyone. Havin' a good time, are ya?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I love Duloc, first of all. Very clean.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "What are you doing here?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Really, it's rude enough being alive when no one wants you, but showing up uninvited to a wedding-", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Fiona! I need to talk to you.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, now you wanna talk? Well, it's a little late for that, so if you'll excuse me-", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "But you can't marry him.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "And why not?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Because- Because he's just marrying you so he can be king.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Outrageous! Fiona, don't listen to him.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "He's not your true love.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "And what do you know about true love?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Well, I- Uh- I mean-", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, this is precious.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Chuckling] The ogre has fallen in love with the princess! Oh, good Lord.", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Crowd Laughing]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "An ogre and a princess! [Laughing]", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek, is this true?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Who cares? It's preposterous! Fiona, my love, we're but a kiss away from our 'happily ever after.' Now kiss me! Mmmm!", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "'By night one way, by day another.' I wanted to show you before.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Sparkling]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Crowd Gasping]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Well, uh, that explains a lot.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Ugh! It's disgusting! Guards! Guards! I order you to get them out of my sight now!", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Get them! Get them both!", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "No, no! Shrek!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "This hocus-pocus alters nothing. This marriage is binding, and that makes me king! See? See!?", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "No, let go of me! Shrek!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "No!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Don't just stand there, you morons!", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Get out of my way! Fiona!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Metal Clanking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Arrgh!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I'll make you regret the day we met. I'll see you drawn and quartered! You'll beg for death to save you!", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "No! Shrek!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "And as for you, my wife...", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Fiona!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I'll have you locked back in that tower for the rest of your days! I am king!", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Whistles]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I will have order! I will have perfection! I will have-", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Roaring]", r = 0.60, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Glass Shattering]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Aaah!", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "*Chomp!*", r = 0.60, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Aah!", r = 1.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "All right. Nobody move. I got a dragon here, and I'm not afraid to use it.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Roars]", r = 0.60, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "I'm a donkey on the edge!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Belches]", r = 0.60, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Crown Clanking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Laughs] Celebrity marriages. They never last, do they?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Crowd Cheering]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Go ahead, Shrek.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Uh, Fiona?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Yes, Shrek?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "I... I love you.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Really?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Really, really.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Chuckles] I love you too.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[Kiss]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Crowd Awws]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Sparkling]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Echoing] 'Until you find true love's first kiss, and then take love's true form.'", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
