		{ text = "[Glass Shattering]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Fiona? Fiona. Are you all right?", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Well, yes. But I don't understand. I'm supposed to be beautiful.", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "But you are beautiful.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Chuckles]", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "I was hoping would be a happy ending.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
