		{ text = "[img=music] Your fallen tears have called to me, so here comes my sweet remedy. [img=music]", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "[img=music] I know what every princess needs, for her to live life happily. [img=music]", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
