		{ text = "With... just... a...", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "[img=music] Wave of my magic wand! Your troubles will soon be gone! [img=music]", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "[img=music] With a flick of the wrist and just a flash, you'll land a prince with a ton of cash! [img=music]", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "[img=music] A high-priced dress made by mice no less, some crystal glass pumps and no more stress. [img=music]", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "[img=music] Your worries will vanish, your soul will cleanse. Confide in your very own furniture friends. [img=music]", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "[img=music] We'll help you set a new fashion trend! [img=music]", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "Whoa!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] I'll make you fancy, I'll make you great! [img=music]", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "[img=music] The kind of girl a prince would date! [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] They'll write your name on the bathroom wall... [img=music]", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "[img=music] For a happy ever after, give Fiona a call! [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] A sporty carriage to ride in style. Sexy man boy chauffeur, Kyle [img=music]", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "[img=music] Banish your blemishes, tooth decay, cellulite thighs will fade away! [img=music]", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "[img=music] And oh, what the hey! Have a bichon frise! [img=music]", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "[img=music] Nip and tuck, here and there to land that prince with the perfect hair. [img=music]", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "[img=music] Lipstick liners, shadows blush, to get that prince with the sexy tush. [img=music]", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
