		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Princess!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "[Gasps] Donkey?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Wow! That potion worked on you too, huh?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "What potion?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, it's kind of a long story but, see, Shrek and I took some magic potion. And well, now... we're sexy!", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
