		{ text = "[Purring]", r = 1.00, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 1.00, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "For you, baby... I could be.", r = 1.00, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "Donkey, where is Shrek?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
