		{ text = "Shrek!", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Fiona! Fiona!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Furniture Shuffling]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "You want to dance, pretty boy?", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Door Slams Shut]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Are you going so soon? Don't you want to see your wife?", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Fiona?", r = 1.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Aye, Fiona. It is me.", r = 1.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "What happened to your voice?", r = 0.00, g = 0.60, b = 0.00, codes = "BOR-1" },
		{ text = "Uh... the potion changed a lot of things, Fiona, but not the way I feel about you.", r = 1.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Fiona?", r = 1.00, g = 0.00, b = 0.50, codes = "BOR-1" },
		{ text = "Charming!?", r = 0.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "Ah- Do you think so? [Chuckling]", r = 1.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Dad, I was so hoping you would approve.", r = 1.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Um... who are you?", r = 1.00, g = 0.00, b = 0.50, codes = "BOR-1" },
		{ text = "Mom, it's me, Shrek.", r = 1.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I know you never get a second chance at a first impression, but, well... what do you think?", r = 1.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Fiona! Fiona!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Fiona! Fiona!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Fiona!", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Fiona, Fiona!", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "Ho-ho-ho-ho, shoot! I don't think they can hear us, pigeon.", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "[Sighs]", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Don't you think you've already messed her life up enough?", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "I just wanted her to be happy.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "And now she can be. Oh, sweetheart. She's finally found the prince of her dreams.", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "But look at me. Look what I've done for her.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "It's time you stop living in a fairy tale, Shrek. She's a princess, and you're an ogre.", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "That's something no amount of potion is ever going to change.", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "But... I love her.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "If you really love her... you'll let her go.", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Shrek?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Senor?", r = 1.00, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "Hey, what's going on? Where are you going?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "You wouldn't have had anything to do with this... would you, Harold?", r = 1.00, g = 0.00, b = 0.50, codes = "BOR-1" },
		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Piano Playing]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[img=music] People just ain't no good. I think that's well understood. [img=music]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "There you go, boys.", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Just leave the bottle, Doris.", r = 1.00, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "Hey. Why the long face?", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "It was all just a stupid mistake. I never should have rescued her from that tower in the first place.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Cup Clanking]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Sighs] I hate Mondays.", r = 1.00, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "I can't believe you're just gonna walk away from the best thing that ever happened to you.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "What choice do I have? She loves that pretty boy, Prince Charming.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Aw, come on now. Is he really that good-looking?", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Are you kidding? He's gorgeous! He has a face that looks like it was carved by angels.", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh. He sounds dreamy.", r = 1.00, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "You know, shockingly, this isn't making me feel any better.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Sighs] Look, guys. It's for the best. Mom and Dad approve, and Fiona gets the man she's always dreamed of.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Everybody wins.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "[Door Opening]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Except for you. I don't get it, Shrek. You love Fiona.", r = 0.40, g = 0.40, b = 0.40, codes = "BOR-1" },
		{ text = "Aye. And that's why I have to let her go.", r = 0.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Umm... excuse me, is she here?", r = 0.00, g = 0.00, b = 1.00, codes = "BOR-1" },
