		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh, hello again. Heh...", r = 0.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "[Door Closing]", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Fairy Godmother. Charming.", r = 0.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "Ugh, you'd better have a good reason for dragging us down here, Harold.", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "Well, I-I'm afraid Fiona isn't really... warming up to Prince Charming.", r = 0.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "Umm, FYI, not my fault.", r = 1.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh no, no, of course it's not, dear.", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "I mean, how charming can I be when I have to pretend I'm that dreadful ogre?", r = 1.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "No, no, it's nobody's fault. Um, perhaps it's best if we just call the whole thing off, okay?", r = 0.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "What!?", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "What!?", r = 1.00, g = 1.00, b = 0.00, codes = "BOR-1" },
		{ text = "I mean, you can't force someone to fall in love!", r = 0.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "Oh, I beg to differ. I do it all the time.", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "Have Fiona drink this and she'll fall in love with the first man she kisses, which will be Charming.", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "Um... n-no.", r = 0.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "What did you say?", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "I-I-I can't. I-I won't do it.", r = 0.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "Oh, yes, you will. If you remember, I helped you with your happily ever after. And I can take it away just as easily.", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "Is that what you want? Is it?", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "N-No...", r = 0.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "Good boy. Now, we have to go. I need to do Charming's hair before the ball.", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "He's hopeless. Y'know he's all high in the front, he can never get to the back. You always need someone to do the back of your hair.", r = 1.00, g = 0.00, b = 1.00, codes = "BOR-1" },
		{ text = "Oh! Thank you, mother.", r = 1.00, g = 1.00, b = 0.00, codes = "BOR-1" },
