		{ text = "Everyone who's anyone has turned out to honor Princess Fiona and Prince Shrek and, oh my, the outfits look gorgeous!", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Look! Hansel and Gretel! What the heck are the crumbs for?", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "And right behind them, Tom Thumb and Thumbelina! Oh, aren't they adorable!", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
