		{ text = "Here comes Sleeping Beauty! Oh! Tired old thing...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh! Who's this? Who's this?! Who is this?", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "Oh. It's the one, it's the only... it's the Fairy Godmother!", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
