		{ text = "...", r = 0.00, g = 0.00, b = 0.00, codes = "BOR-1" },
		{ text = "We'll be right back with the Royal Far Far Away Ball after these messages.", r = 0.90, g = 0.90, b = 1.00, codes = "BOR-1" },
		{ text = "I hate these ball shows! They bore me to tears. Flip over to Wheel Of Torture.", r = 0.70, g = 0.50, b = 0.00, codes = "BOR-1" },
		{ text = "I'm not flipping anywhere, sir, until I see Shrek and Fiona.", r = 0.80, g = 0.80, b = 0.25, codes = "BOR-1" },
